package day2;

public class DemoNestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int a=30, b=20, c=10;
   if (a>b)
   {
	   if(a>c)
	   {
		   System.out.println("a is largest");
	   }
	   else
	   {
		   System.out.println("c is largest");
	   }
   }
   else {
	   System.out.println("b is largest");
   }
	  
		   
   }
	}


